import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

// A simple CSS style for our custom error messages
const errorStyle = {
  color: 'red',
  fontSize: '0.875em',
  marginTop: '5px'
};

function AddProduct() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    category_id: '',
    image_url: ''
  });
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [apiError, setApiError] = useState(''); // Renamed for clarity
  const [formErrors, setFormErrors] = useState({}); // New state for form validation errors
  const navigate = useNavigate();

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await api.get('/products/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Optional: clear error when user starts typing
    if (formErrors[e.target.name]) {
        setFormErrors({
            ...formErrors,
            [e.target.name]: null
        });
    }
  };

  // --- New Validation Function ---
  const validateForm = () => {
    const errors = {};
    if (!formData.title.trim()) errors.title = 'Product title is required.';
    if (!formData.category_id) errors.category_id = 'Please select a category.';
    if (!formData.description.trim()) errors.description = 'Description is required.';
    if (!formData.price || parseFloat(formData.price) <= 0) errors.price = 'Please enter a valid price greater than 0.';
    // you could add more complex validation here, e.g., for the URL format
    return errors;
  };

  // --- Updated handleSubmit Function ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    setApiError(''); // Clear previous API errors

    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setFormErrors(newErrors);
      return; // Stop the submission if there are validation errors
    }

    setLoading(true);
    setFormErrors({}); // Clear form errors before submitting

    try {
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        category_id: parseInt(formData.category_id)
      };
      
      await api.post('/products/', productData);
      navigate('/my-listings');
    } catch (error) {
      setApiError(error.response?.data?.detail || 'Failed to create product');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container" style={{ padding: '40px 20px' }}>
      <button 
        onClick={() => navigate('/dashboard')} 
        className="btn btn-secondary"
        style={{ marginBottom: '30px' }}
      >
        ← Back to Dashboard
      </button>

      <div className="form-container">
        <h1 className="form-title">Add New Product</h1>
        <p style={{ textAlign: 'center', color: '#666', marginBottom: '30px' }}>
          List your item for sale and help build a sustainable marketplace
        </p>

        {apiError && <div className="error">{apiError}</div>}

        {/* Note: noValidate disables the browser's default validation popups */}
        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group">
            <label className="form-label">Product Title *</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter a descriptive title for your product"
            />
            {formErrors.title && <div style={errorStyle}>{formErrors.title}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Category *</label>
            <select
              name="category_id"
              value={formData.category_id}
              onChange={handleChange}
              className="form-input"
            >
              <option value="">Select a category</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
            {formErrors.category_id && <div style={errorStyle}>{formErrors.category_id}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-textarea"
              placeholder="Describe your product in detail. Include condition, size, brand, etc."
              rows="5"
            />
            {formErrors.description && <div style={errorStyle}>{formErrors.description}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Price ($) *</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              className="form-input"
              placeholder="0.00"
              step="0.01"
              min="0"
            />
            {formErrors.price && <div style={errorStyle}>{formErrors.price}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Image URL (Optional)</label>
            <input
              type="url"
              name="image_url"
              value={formData.image_url}
              onChange={handleChange}
              className="form-input"
              placeholder="https://example.com/image.jpg"
            />
            <small style={{ color: '#666', marginTop: '5px', display: 'block' }}>
              You can add an image URL for your product. For now, we'll use a placeholder if no URL is provided.
            </small>
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            style={{ width: '100%', marginBottom: '20px' }}
            disabled={loading}
          >
            {loading ? 'Creating Listing...' : 'Create Listing'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default AddProduct;
